import React, { useEffect, useState } from "react";
import "./TrendingPage.css";
import productImage from "../Assets/products/1.jpg";
import TrendingItems from "./trendingItems.json";

const TrendingPage = () => {
    const [pageNum, setPageNum] = useState(1)
  return (
    <>
      <div class="container">
        {TrendingItems.products.map((value) => {
          return (
            <div className="productList">
              {value.recommended ? (
                <div className="discount">
                  <div className="discount-text">Recommended</div>
                </div>
              ) : (
                ""
              )}
              <div class="product">
                <img src={productImage} alt="Product 1" />
                <div class="product-info">
                  <h3>{value.name}</h3>
                  <p>{value.description}</p>
                </div>
                {value.newPrice ? (
                  <p class="price">
                    {" "}
                    <span style={{ fontSize: "12px" }}>
                      <s>
                        {value.price}

                        <sup>{value.currency}</sup>
                      </s>
                    </span>
                    &nbsp;&nbsp;
                    <span style={{ color: "green" }}>
                      {value.newPrice}
                      <sup>{value.currency}</sup>
                    </span>
                  </p>
                ) : (
                  <p class="price">
                    {value.price}
                    <sup>{value.currency}</sup>
                  </p>
                )}
                <div className="buttons">
                  <div className="add-to-cart">Add to cart</div>
                  <div className="order-now">Order Now</div>
                </div>
              </div>
            </div>
          );
        })}
        <div className="pagination">
            <div className="pagination-wrapper">
                <span className="pagination-btn ">{"<"}&nbsp;Back</span>
                <span className="pagination-btn active-page">1</span>
                <span className="pagination-btn">2</span>
                <span className="pagination-btn">3</span>
                <span className="pagination-btn">4</span>
                <span className="pagination-btn">5</span>
                <span className="pagination-btn">Next&nbsp;{">"}</span>
            </div>
        </div>
      </div>
    </>
  );
};

export default TrendingPage;
