import React, { useEffect, useState } from "react";
import "./Cart.css";
import cartItems from "./cartItems.json";
import productImage from "../Assets/products/1.jpg";

const Cart = () => {
  return (
    <div className="cart-container">
      <div className="text-section">
        <i class="fa-solid fa-cart-shopping"></i>&nbsp;My Cart
      </div>
      <div className="main-cart">
        <div className="cart-list">
          {cartItems.products.map((value) => {
            return (
              <>
                <div className="cart-item">
                  <div className="cart-image">
                    <img src={productImage} alt="" className="item-img" />
                  </div>
                  <div className="item-details">
                    <div className="heading">{value.name}</div>
                    <div className="category-heading">{value.category}</div>
                    <div className="price-item">
                      <div className="price-content">
                        <table border="1" >
                          <tr>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th>Total</th>
                          </tr>
                          <tr>
                            <td>{value.currency}{value.newPrice ? value.newPrice : value.price}</td>
                            <td contentEditable onChange={()=>alert('hello')}>{value.quantity}</td>
                            <td>{value.newPrice ? value.newPrice * value.quantity : value.price * value.quantity}{value.currency}</td>
                          </tr>
                        </table>

                      </div>
                    </div>
                  </div>
                </div>
                <hr />
              </>
            );
          })}
        </div>
        <div className="cart-checkout">checkout</div>
      </div>
    </div>
  );
};

export default Cart;
