import React, { useState } from "react";
import "./Navbar.css";
import logo from "../Assets/logo.png";
import cart_icon from "../Assets/cart.png";
import { Link } from "react-router-dom";

const Navbar = () => {
  const [menu, setMenu] = useState("shop");
  const [profileDropdown, setProfileDropdown] = useState(false)

  const handleProfileDropDown = ()=>{
      if(profileDropdown){
        setProfileDropdown(false)
      }
      else{
        setProfileDropdown(true)
      }
  }

  const handleSignup = ()=>{
    handleProfileDropDown()
  }

  const handleSignin = ()=>{
    handleProfileDropDown()
  }
  return (
    <div>
      <div className="top-sidebar"> </div>
      <div className="action-buttons-for-mobile">
        <div className="list-for-mobile">
          <span className="text-label-for-mobile">Your Orders</span>
        </div>
      </div>

    <header>
      <div class="logo">
        <img src={logo} alt="Logo" />
        <div class="site-name">SHRINGAR.COM</div>
      </div>

      <div class="search-bar">
        <input type="text" class="search-input" placeholder="Search..." />
      </div>
      <div className="bars">
        <i class="fa-solid fa-bars"></i>
      </div>
      <div class="action-buttons">
        <span className="nav-icons">
          <span className="nav-text-label">Your Orders</span>
        </span>
        <span className="nav-icons-2">
          <i class="fa-solid fa-circle-user hover-class" onClick={()=>{handleProfileDropDown()}}></i>
          {profileDropdown ?  
          <div className="dropdownProfile">
           <div className="signup-login">
           <div className="list-item" style={{paddingTop:'10px'}}>Welcome User</div>
           <hr/>

            <div className="list-item list-item-2 hover-class-2" onClick={()=>handleSignup()}>

              Sign up</div>
            <div className="list-item list-item-2 hover-class-2" onClick={()=>handleSignin()}>
              Login</div>

            </div>
          </div>: ""}
        </span>
        <span className="nav-icons">
          <span className="cart-count">10</span>
          <i class="fa-solid fa-cart-shopping"></i>
        </span>
      </div>

    </header>
    <div className="sub-header">
    <div className={`list ${window.location.pathname == '/' ? 'active' : ''}`}>Home </div>
    <div className="list">Trending</div>
    <div className="list">Categories</div>
    <div className="list">New Arrivals</div>
    </div>

    </div>
  );
};

export default Navbar;
